INSERT INTO `jiugou` VALUES ('1', '张家界国际大酒店', '张家界市区', '中外合资旅游酒店。设有标准客房、豪华客房、商务楼层及总统套间，共226间（套）；与之配套的有中餐厅、西餐厅、风味厅、酒吧、咖啡厅、宴会厅、多功能国际会议中心、商务中心、美容美发中心、桑拿按摩中心、豪华歌舞厅、室外游泳池、网球场、球艺室及棋牌室等各类娱乐服务设施，一应俱全。', '★★★★ 四星级', '14667660032722.jpg', '双标间', '350', '350', '在线支付');
INSERT INTO `jiugou` VALUES ('2', '张家界阳光酒店', '张家界市区', '五星级标准豪华会展旅游度假酒店。地处张家界市新城区，南望天门，北依澧水，毗邻长张高速出口、永定区政府，接轨市中心主干道永定大道，距火车站、飞机场十五分钟车程、核心景区30分钟车程，地理交通位置极为便捷。', '★★★★ ★ 五星级', '14667663153068.jpg', '双标间', '550', '550', '在线支付');
INSERT INTO `jiugou` VALUES ('3', '张家界大成山水国际大酒店', '张家界市区', '【规模最大】 张家界地标性建筑，高度超过80米，跨度超过300米 张家界单体面积最大酒店，达82777.5平方米 张家界唯一采用全玻璃幕墙建筑 【设施最好】 张家界最典雅气派的酒店大堂，面积超过4000平方米 张家界最大的国际会议中心，可同时容纳2000人会议 张家界最大的宴会厅，可同时设宴120席 张家界最大餐饮包厢。', '★★★★ 四星级', '14667664683621.jpg', '双标间', '480', '480', '在线支付');
INSERT INTO `jiugou` VALUES ('4', '张家界通达国际酒店', '张家界市区', '位于张家界市区的四星级豪华酒店，背依世界著名的旅游风景区——天门山国家森林公园；日观天门胜景，夜伴澧水涛声。 张家界通达国际酒店是一家高端旅游商务型酒店，装潢雅致，设施完善。酒店拥有温馨舒适的各类客房；同时配设中西餐厅、健身房等配套设施，是您旅游度假、商务洽谈的理想场所。', '★★★★ 四星级', '14667665622424.jpg', '双标间', '280', '280', '在线支付');
INSERT INTO `jiugou` VALUES ('5', '张家界蓝天大酒店', '张家界市区', '由张家界军分区兴建的一家集客房、餐饮、会议、休闲为一体的豪华旅游度假酒店，距飞机场仅10分钟车程、火车站和常张高速路口仅5分钟车程、核心景区30分钟左右车程，地理交通位置极其便捷。是省委、省政府和张家界市各行政机关、公司企业的指定接待单位。', '★★★★ 四星级', '14667666536546.jpg', '双标间', '380', '380', '在线支付');
INSERT INTO `jiugou` VALUES ('6', '张家界闽南国际酒店', '张家界市区', '张家界闽南国际酒店位于张家界市区繁华路段。酒店设备先进，功能齐全，各类设施一应俱全。酒店交通十分便利，离飞机场和火车站仅10分钟车程，距张家界核心景区约30分钟车程。 张家界闽南国际酒店酒店拥有多种类型客房，房间内设施十分齐全，尽享舒适自在，可满足不同类型宾客的需求。', '★★★ 三星级', '14667667074775.jpg', '双标间', '248', '248', '在线支付');
INSERT INTO `jiugou` VALUES ('7', '张家界紫御大酒店', '张家界市区', '集商务、团队、会议、休闲娱乐于一体的商务型综合酒店。距火车站10分钟车程、飞机场15分钟车程、武陵源核心景区30分钟车程。', '★★★★ 四星级', '14667667575347.jpg', '双标间', '300', '300', '在线支付');
INSERT INTO `jiugou` VALUES ('8', '张家界京武铂尔曼酒店', '张家界市区', '由法国雅高酒店管理集团管理，是目前张家界地区唯一国际五星级酒店酒店，距张家界市区、飞机场、火车站30分钟车程，到长沙全程高速，仅需3.5小时车程。酒店周边美景如云，乘车到张家界国家森林公园天子山入口、黄龙洞、宝峰湖仅需几分钟车程，交通便利。', '★★★★ ★ 五星级', '14667668149647.jpg', '票种', '780', '780', '在线支付');
INSERT INTO `jiugou` VALUES ('9', '张家界京溪国际酒店', '张家界景区', '由司法系统主办的综合性涉外豪华酒店，距张家界机场、火车站30分钟车程，距宝峰湖、黄龙洞、天子山景区仅5分钟车程。', '★★★★ 四星级', '14667669484423.jpg', '双标间', '380', '380', '在线支付');

